"use client";

import { motion, useInView } from "framer-motion";
import { useRef, useState } from "react";
import { Spotlight } from "@/components/ui/spotlight";
import { BorderBeam } from "@/components/ui/border-beam";
import { NumberTicker } from "@/components/ui/counter";
import { useTheme } from "next-themes";
import { cn } from "@/lib/utils";
import {
  Users,
  Award,
  Star,
  Briefcase,
  Code,
  Globe,
  Rocket,
  Target,
} from "lucide-react";

interface StatItemProps {
  value: number;
  label: string;
  icon: React.ReactNode;
  delay?: number;
  decimalPlaces?: number;
}

interface AboutUs2Props {
  title?: string;
  subtitle?: string;
  description?: string;
  stats?: StatItemProps[];
  className?: string;
}

const defaultStats = [
  {
    value: 5000,
    label: "Happy Clients",
    icon: <Users className="h-6 w-6" />,
    delay: 0,
  },
  {
    value: 15,
    label: "Years Experience",
    icon: <Briefcase className="h-6 w-6" />,
    delay: 0.1,
  },
  {
    value: 100,
    label: "Projects Completed",
    icon: <Code className="h-6 w-6" />,
    delay: 0.2,
  },
  {
    value: 24,
    label: "Awards Won",
    icon: <Award className="h-6 w-6" />,
    delay: 0.3,
  },
];

const StatItem = ({ value, label, icon, delay = 0, decimalPlaces = 0 }: StatItemProps) => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, amount: 0.3 });
  const { resolvedTheme } = useTheme();

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, y: 20 }}
      animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
      transition={{ duration: 0.6, delay: delay, ease: "easeOut" }}
      className={cn(
        "relative overflow-hidden rounded-xl border border-primary/10 bg-background/50 p-6 backdrop-blur-sm",
        resolvedTheme === "dark" ? "shadow-lg shadow-primary/5" : "shadow-md shadow-primary/10"
      )}
    >
      <div className="absolute -right-4 -top-4 h-16 w-16 rounded-full bg-primary/10 opacity-70 blur-xl" />

      <div className="mb-4 flex h-12 w-12 items-center justify-center rounded-lg bg-primary/10">
        <div className="text-primary">{icon}</div>
      </div>

      <div className="flex flex-col gap-1">
        <h3 className="text-3xl font-bold tracking-tight">
          <NumberTicker 
            value={value} 
            decimalPlaces={decimalPlaces}
            className="text-foreground"
          />
          <span className="ml-1 text-primary">+</span>
        </h3>
        <p className="text-sm text-muted-foreground">{label}</p>
      </div>
    </motion.div>
  );
};

export default function AboutUs2({ 
  title = "About Our Company",
  subtitle = "Delivering excellence for over 15 years",
  description = "We are a passionate team of experts dedicated to delivering exceptional solutions that help businesses thrive in the digital landscape. Our commitment to innovation and quality has made us a trusted partner for organizations worldwide.",
  stats = defaultStats,
  className,
}: AboutUs2Props) {
  const aboutRef = useRef(null);
  const statsRef = useRef(null);

  const aboutInView = useInView(aboutRef, { once: true, amount: 0.3 });
  const statsInView = useInView(statsRef, { once: true, amount: 0.3 });

  return (
    <section className={cn("relative overflow-hidden py-20", className)}>
      <Spotlight
        className="-top-40 left-0 md:left-60"
        gradientFirst="radial-gradient(68.54% 68.72% at 55.02% 31.46%, hsla(336, 100%, 50%, 0.08) 0, hsla(341, 100%, 55%, 0.04) 50%, hsla(336, 100%, 45%, 0) 80%)"
        gradientSecond="radial-gradient(50% 50% at 50% 50%, hsla(333, 100%, 85%, 0.08) 0, hsla(335, 100%, 55%, 0.04) 80%, transparent 100%)"
        gradientThird="radial-gradient(50% 50% at 50% 50%, hsla(332, 100%, 85%, 0.06) 0, hsla(327, 100%, 85%, 0.06) 80%, transparent 100%)"
      />

      <div className="container relative z-10 mx-auto px-4 md:px-6">
        {/* Header Section */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="mx-auto mb-16 max-w-3xl text-center"
        >
          <h1 className="bg-gradient-to-r from-foreground/80 via-foreground to-foreground/80 bg-clip-text text-4xl font-bold tracking-tight text-transparent sm:text-5xl md:text-6xl">
            {title}
          </h1>
          <p className="mt-4 text-xl text-muted-foreground">{subtitle}</p>
        </motion.div>

        {/* Stats Section */}
        <div 
          ref={statsRef} 
          className="mb-20"
        >
          <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-4">
            {stats.map((stat, index) => (
              <StatItem
                key={index}
                value={stat.value}
                label={stat.label}
                icon={stat.icon}
                delay={stat.delay || index * 0.1}
                decimalPlaces={stat.decimalPlaces}
              />
            ))}
          </div>
        </div>

        {/* About Content Section */}
        <div 
          ref={aboutRef}
          className="relative mx-auto max-w-6xl"
        >
          <motion.div
            initial={{ opacity: 0, y: 40 }}
            animate={aboutInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 40 }}
            transition={{ duration: 0.8, delay: 0.2, ease: "easeOut" }}
            className="relative overflow-hidden rounded-2xl border border-border/40 bg-gradient-to-br from-background/80 to-background/40 p-8 backdrop-blur-3xl md:p-12"
          >
            <BorderBeam
              duration={8}
              size={300}
              className="from-transparent via-primary/30 to-transparent"
            />

            <div className="grid gap-10 md:grid-cols-2">
              <div className="space-y-6">
                <div className="inline-flex h-16 w-16 items-center justify-center rounded-2xl bg-gradient-to-br from-primary/20 to-primary/5 backdrop-blur-sm">
                  <Rocket className="h-8 w-8 text-primary" />
                </div>

                <h2 className="text-3xl font-bold tracking-tight">Our Mission</h2>

                <p className="text-lg leading-relaxed text-muted-foreground">
                  To empower businesses with innovative digital solutions that drive growth, 
                  enhance user experiences, and create lasting value in an ever-evolving 
                  technological landscape.
                </p>
              </div>

              <div className="space-y-6">
                <div className="inline-flex h-16 w-16 items-center justify-center rounded-2xl bg-gradient-to-br from-blue-500/20 to-blue-500/5 backdrop-blur-sm">
                  <Target className="h-8 w-8 text-blue-500" />
                </div>

                <h2 className="text-3xl font-bold tracking-tight">Our Vision</h2>

                <p className="text-lg leading-relaxed text-muted-foreground">
                  To be the leading provider of transformative digital experiences, 
                  recognized globally for our commitment to excellence, innovation, 
                  and client success.
                </p>
              </div>
            </div>

            <div className="mt-10">
              <p className="text-lg leading-relaxed text-muted-foreground">
                {description}
              </p>
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
}
