import React from "react";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { DropdownMenu, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";

interface SkeletonTableProps {
    rowCount?: number;
    columnCount?: number;
    showTopBar?: boolean;
    showRealFilter?: boolean;
    showRealColumButton?: boolean;
    bodyClassName?: string;
    tableHeadings?: string[];
}

interface ShimmerComponentProps {
    className?: string;
}

const ShimmerComponent = ({ className = "" }: ShimmerComponentProps) => {
    return <div className={`animate-pulse rounded bg-gray-500 ${className}`} />;
};

const RealTopBar = ({
    showFilter,
    showColumnToggle,
}: {
    showFilter: boolean;
    showColumnToggle: boolean;
}) => (
    <div className="flex items-center py-4">
        {showFilter && <Input placeholder="Filter values" className="max-w-sm" />}
        {showColumnToggle && (
            <DropdownMenu>
                <div className="ml-auto">
                    <DropdownMenuTrigger asChild>
                        <Button variant="outline">Columns</Button>
                    </DropdownMenuTrigger>
                </div>
            </DropdownMenu>
        )}
    </div>
);

const TableHeading = ({
    columnCount,
    showTableHeading,
    tableHeadings,
}: {
    columnCount: number,
    showTableHeading: boolean,
    tableHeadings: string[]
}) => {

    const headings = showTableHeading
        ? (tableHeadings?.length === columnCount
            ? tableHeadings
            : Array.from({ length: columnCount }, () => "â€”"))
        : [];

    return (
        <div className="flex items-center border-b-2 h-10 mb-1 px-2">
            {headings.map((heading, idx) => (
                <div key={idx} className="w-full flex justify-center">
                    <h3 className="font-semibold">{heading}</h3>
                </div>
            ))}
        </div>
    )
}



const ShimmerTable = ({
    rowCount,
    columnCount,
    headings,
}: {
    rowCount: number;
    columnCount: number;
    headings: string[] | undefined;
}) => {

    return (
        <div className="flex flex-col rounded-md overflow-hidden border-2 w-full">
            {headings && (
                <TableHeading columnCount={columnCount} showTableHeading={true} tableHeadings={headings}/>
            )}
            {Array.from({ length: rowCount }).map((_, rowIdx) => (
                <div
                    key={rowIdx}
                    className={`h-10 flex items-center mb-1 px-2 ${rowCount - rowIdx === 1 ? "border-b-0" : "border-b-2"
                        }`}
                >
                    {Array.from({ length: columnCount }).map((_, colIdx) => (
                        <ShimmerComponent key={colIdx} className="h-3 w-full mx-2" />
                    ))}
                </div>
            ))}
        </div>
    );
};

export default function SkeletonTableWrapper({
    rowCount = 5,
    columnCount = 5,
    showTopBar = true,
    showRealFilter = true,
    showRealColumButton = true,
    bodyClassName = "px-10",
    tableHeadings,
}: SkeletonTableProps) {

    return (
        <div className={`w-full ${bodyClassName}`}>
            {showTopBar && (
                <RealTopBar
                    showFilter={showRealFilter}
                    showColumnToggle={showRealColumButton}
                />
            )}

            <ShimmerTable
                rowCount={rowCount}
                columnCount={columnCount}
                headings={tableHeadings}
            />
        </div>
    );
}
