interface SkeletonTableInterface {
    rowCount?: number;
    columnCount?: number;
    showTopBar?: boolean;
}

export default function SkeletonTable({
    rowCount = 5,
    columnCount = 4,
    showTopBar = true,
}: SkeletonTableInterface) {
    return (
        <>
            {showTopBar && (
                <div className="flex flex-col rounded-md overflow-hidden mb-2">
                    <div className="h-15 flex items-center justify-between">
                        <div className="h-8 w-4/12 shimmer rounded"></div>
                        <div className="h-8 w-1/12 shimmer rounded"></div>
                    </div>
                </div>
            )}

            <div className="flex flex-col rounded-md overflow-hidden border">
                {Array.from({ length: rowCount }).map((_, rowIdx) => (
                    <div key={rowIdx} className="h-10 flex items-center border-b mb-1 px-2">
                        {Array.from({ length: columnCount }).map((_, colIdx) => (
                            <div key={colIdx} className="h-5 w-1/5 shimmer mx-2 rounded"></div>
                        ))}
                    </div>
                ))}
            </div>
        </>
    );
}
