import { useState } from "react";

export default function TechnicalPricing() {

  const [isAnnual, setIsAnnual] = useState(true);
  const prices = isAnnual
    ? ["$15", "$39", "$159"]
    : ["$19", "$49", "$199"];

  return (
    <>
      <style jsx global>{`
        body {
          margin: 0;
          background: #ffffff;
          font-family: 'Courier New', monospace;
        }
        .dimension-line {
          position: relative;
        }
        .dimension-line::before {
          content: '';
          position: absolute;
          background: #000;
          z-index: 1;
        }
        .dimension-h::before {
          height: 1px;
          width: 20px;
          top: 50%;
          left: -30px;
        }
        .dimension-v::before {
          width: 1px;
          height: 20px;
          left: 50%;
          top: -30px;
        }
        .dimension-bracket {
          position: relative;
        }
        .dimension-bracket::after {
          content: '';
          position: absolute;
          border: 1px solid #000;
          z-index: 1;
        }
        .bracket-top::after {
          top: -15px;
          left: 0;
          right: 0;
          height: 10px;
          border-bottom: none;
        }
        .bracket-left::after {
          left: -15px;
          top: 0;
          bottom: 0;
          width: 10px;
          border-right: none;
        }
        .tech-card {
          border: 2px solid #000;
          background: #fff;
          position: relative;
        }
        .tech-card::before {
          content: '';
          position: absolute;
          top: 10px;
          right: 10px;
          width: 8px;
          height: 8px;
          border: 1px solid #000;
          background: #fff;
        }
        .popular-card {
          border: 3px solid #000;
          background: #000;
          color: #fff;
        }
        .popular-card::before {
          background: #000;
          border-color: #fff;
        }
        .grid-bg {
          background-image: linear-gradient(to right, #e5e5e5 1px, transparent 1px),
            linear-gradient(to bottom, #e5e5e5 1px, transparent 1px);
          background-size: 20px 20px;
        }
      `}</style>

      <div className="grid-bg relative w-full min-h-screen flex flex-col items-center justify-center px-4 py-8">
        <div className="text-center mb-8 dimension-line dimension-v">
          <div className="inline-block border border-black px-4 py-2 mb-4">
            <span className="text-xs tracking-wider">SPECIFICATION SHEET</span>
          </div>
          <h1 className="text-4xl font-bold text-black mb-3 tracking-wider">PRICING MATRIX</h1>
          <p className="text-black text-base max-w-xl mx-auto px-4 font-mono">
            TECHNICAL CONFIGURATIONS &amp; RESOURCE ALLOCATION
          </p>
        </div>

        <div className="mb-10 flex items-center justify-center gap-4 dimension-line dimension-h">
          <span className="text-black text-sm font-mono">MONTHLY</span>
          <button
            type="button"
            aria-label="Toggle monthly / annual pricing"
            onClick={() => setIsAnnual((prev) => !prev)}
            className="relative inline-block w-12 h-6 bg-white border-2 border-black cursor-pointer focus:outline-none"
          >
            <span
              className="absolute top-0 w-6 h-6 bg-white border border-black transition-transform duration-300"
              style={{ transform: isAnnual ? 'translateX(24px)' : 'translateX(0)' }}
            />
          </button>
          <span className="text-black text-sm font-mono">
            ANNUAL <span className="text-xs">[-20%]</span>
          </span>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 w-full max-w-6xl dimension-bracket bracket-top">
          <div className="tech-card p-6 flex flex-col dimension-line dimension-h">
            <div className="flex items-center mb-6">
              <div className="w-10 h-10 border border-black flex items-center justify-center bg-white">
                <i className="fas fa-rocket text-black text-sm" />
              </div>
              <div className="ml-4">
                <h3 className="text-xl font-bold text-black tracking-wider">STARTER</h3>
                <span className="text-xs font-mono">CONFIG-001</span>
              </div>
            </div>
            <div className="mb-6 border-b border-black pb-4">
              <div className="flex items-baseline">
                <span className="price text-4xl font-bold text-black font-mono">{prices[0]}</span>
                <span className="text-black ml-2 text-sm font-mono">/MONTH</span>
              </div>
              <p className="text-black text-sm mt-1 font-mono">INDIVIDUAL DEPLOYMENT</p>
            </div>
            <ul className="space-y-3 mb-8 flex-grow text-sm font-mono">
              <li className="flex items-center text-black">
                <span className="mr-3 w-3 text-xs">▪</span>1M TOKENS/MONTH
              </li>
              <li className="flex items-center text-black">
                <span className="mr-3 w-3 text-xs">▪</span>5 CUSTOM MODELS
              </li>
              <li className="flex items-center text-black">
                <span className="mr-3 w-3 text-xs">▪</span>BASIC API ACCESS
              </li>
              <li className="flex items-center text-black">
                <span className="mr-3 w-3 text-xs">▪</span>EMAIL SUPPORT
              </li>
            </ul>
            <button className="w-full py-3 border-2 border-black text-black text-sm font-mono tracking-wider hover:bg-black hover:text-white transition-colors duration-300">
              INITIALIZE
            </button>
            <p className="text-black text-xs text-center mt-2 font-mono">NO PAYMENT REQUIRED</p>
          </div>

          <div className="popular-card p-6 flex flex-col relative transform lg:scale-105 dimension-line dimension-v">
            <span className="absolute -top-4 left-1/2 -translate-x-1/2 bg-black text-white px-4 py-1 text-xs font-mono tracking-wider border border-white">
              RECOMMENDED
            </span>
            <div className="flex items-center mb-6">
              <div className="w-10 h-10 border border-white flex items-center justify-center bg-black">
                <i className="fas fa-bolt text-white text-sm" />
              </div>
              <div className="ml-4">
                <h3 className="text-xl font-bold text-white tracking-wider">PROFESSIONAL</h3>
                <span className="text-xs font-mono text-gray-300">CONFIG-002</span>
              </div>
            </div>
            <div className="mb-6 border-b border-white pb-4">
              <div className="flex items-baseline">
                <span className="price text-4xl font-bold text-white font-mono">{prices[1]}</span>
                <span className="text-white ml-2 text-sm font-mono">/MONTH</span>
              </div>
              <p className="text-white text-sm mt-1 font-mono">TEAM DEPLOYMENT</p>
            </div>
            <ul className="space-y-3 mb-8 flex-grow text-sm font-mono text-white">
              <li className="flex items-center">
                <span className="mr-3 w-3 text-xs">▪</span>10M TOKENS/MONTH
              </li>
              <li className="flex items-center">
                <span className="mr-3 w-3 text-xs">▪</span>20 CUSTOM MODELS
              </li>
              <li className="flex items-center">
                <span className="mr-3 w-3 text-xs">▪</span>ADVANCED API
              </li>
              <li className="flex items-center">
                <span className="mr-3 w-3 text-xs">▪</span>PRIORITY SUPPORT
              </li>
              <li className="flex items-center">
                <span className="mr-3 w-3 text-xs">▪</span>CUSTOM TRAINING
              </li>
            </ul>
            <button className="w-full py-3 bg-white text-black border-2 border-white text-sm font-mono tracking-wider hover:bg-black hover:text-white hover:border-black transition-colors duration-300">
              DEPLOY
            </button>
            <p className="text-gray-300 text-xs text-center mt-2 font-mono">14-DAY TRIAL PERIOD</p>
          </div>

          <div className="tech-card p-6 flex flex-col dimension-line dimension-h">
            <div className="flex items-center mb-6">
              <div className="w-10 h-10 border border-black flex items-center justify-center bg-white">
                <i className="fas fa-building text-black text-sm" />
              </div>
              <div className="ml-4">
                <h3 className="text-xl font-bold text-black tracking-wider">ENTERPRISE</h3>
                <span className="text-xs font-mono">CONFIG-003</span>
              </div>
            </div>
            <div className="mb-6 border-b border-black pb-4">
              <div className="flex items-baseline">
                <span className="price text-4xl font-bold text-black font-mono">{prices[2]}</span>
                <span className="text-black ml-2 text-sm font-mono">/MONTH</span>
              </div>
              <p className="text-black text-sm mt-1 font-mono">ENTERPRISE DEPLOYMENT</p>
            </div>
            <ul className="space-y-3 mb-8 flex-grow text-sm font-mono">
              <li className="flex items-center text-black">
                <span className="mr-3 w-3 text-xs">▪</span>UNLIMITED TOKENS
              </li>
              <li className="flex items-center text-black">
                <span className="mr-3 w-3 text-xs">▪</span>UNLIMITED MODELS
              </li>
              <li className="flex items-center text-black">
                <span className="mr-3 w-3 text-xs">▪</span>FULL API ECOSYSTEM
              </li>
              <li className="flex items-center text-black">
                <span className="mr-3 w-3 text-xs">▪</span>24/7 SUPPORT
              </li>
              <li className="flex items-center text-black">
                <span className="mr-3 w-3 text-xs">▪</span>DEDICATED RESOURCES
              </li>
            </ul>
            <button className="w-full py-3 border-2 border-black text-black text-sm font-mono tracking-wider hover:bg-black hover:text-white transition-colors duration-300">
              CONTACT ENGINEERING
            </button>
            <p className="text-black text-xs text-center mt-2 font-mono">CUSTOM ARCHITECTURE</p>
          </div>
        </div>

        <div className="mt-12 text-center max-w-4xl px-4 dimension-bracket bracket-left">
          <div className="border border-black p-4 bg-white">
            <p className="text-black text-sm mb-4 font-mono">
              ALL CONFIGURATIONS INCLUDE: 99.9% UPTIME SLA • ENTERPRISE SECURITY • COMMUNITY ACCESS
            </p>
            <div className="flex flex-wrap justify-center gap-4">
              <span className="px-3 py-1 border border-black text-black text-xs font-mono">
                GDPR-COMPLIANT
              </span>
              <span className="px-3 py-1 border border-black text-black text-xs font-mono">
                SOC-2-CERTIFIED
              </span>
              <span className="px-3 py-1 border border-black text-black text-xs font-mono">
                HIPAA-READY
              </span>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
